import React, { useState, useEffect, useRef, useContext } from "react";
import { Container, Row, Col, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import calculateAvgRating from "../utils/avgRating";
import "../styles/tour-details.css";
import avatar from "../assets/images/avatar.jpg";
import Booking from "../components/Booking/Booking";
import Newsletter from "../shared/Newsletter";
import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";
import { AuthContext } from './../context/AuthContext'

const TourDetails = () => {
    const { id } = useParams();
    const reviewMsgRef = useRef(null);
    const [tourRating, setTourRating] = useState(null);
    const { user } = useContext(AuthContext);

    const { data: tour, loading, error } = useFetch(`${BASE_URL}/tours/${id}`);

    useEffect(() => {
        if (!loading) window.scrollTo(0, 0);
    }, [loading]);

    if (!tour) {
        return <h2 className="text-center">Tour not found</h2>;
    }

    const { photo, title, desc, price, address, reviews, city, maxGroupSize, distance } = tour;
    const { totalRating, avgRating } = calculateAvgRating(reviews);

    const options = { day: "numeric", month: "long", year: "numeric" };

    const submitHandler = async e => {
        e.preventDefault();
        const reviewText = reviewMsgRef.current.value.trim();

        try {
            if (!user || user === undefined || user === null) {
                alert('Please sign in')
            }
            const reviewObj = {
                username: user.username,
                reviewText,
                rating:totalRating,
            }
            const res = await fetch(`${BASE_URL}/review/${id}`, {
                method:'post',
                headers:{
                    'content-type':'application/json'
                }
            })
        } catch (err) {

        }

        if (!reviewText) return;

        const newReview = {
            text: reviewText,
            rating: tourRating,
            date: new Date().toISOString(),
        };

        console.log("New Review Submitted:", newReview);
        reviewMsgRef.current.value = ""; // Clear input after submission
    };

    return (
        <>
            <section>
                <Container>
                    {loading && <h4 className="text-center pt-5">Loading...</h4>}
                    {error && <h4 className="text-center pt-5">{error}</h4>}
                    {!loading && !error && (
                        <Row>
                            <Col lg="8">
                                <div className="tour__content">
                                    <img src={photo} alt={title} />
                                    <div className="tour__info">
                                        <h2>{title}</h2>
                                        <div className="d-flex align-items-center gap-5">
                                            <span className="tour__rating d-flex align-items-center gap-1">
                                                <i className="ri-star-fill" style={{ color: "var(--secondary-color)" }}></i>
                                                {totalRating === 0 ? "Not Rated" : avgRating}
                                                {reviews?.length > 0 && <span>({reviews.length})</span>}
                                            </span>
                                            <span>
                                                <i className="ri-map-pin-user-fill"></i> {address}
                                            </span>
                                        </div>
                                        <div className="tour__extra-details">
                                            <span>
                                                <i className="ri-map-pin-2-line"></i> {city}
                                            </span>
                                            <span>
                                                <i className="ri-money-dollar-circle-line"></i> ${price}/per person
                                            </span>
                                            <span>
                                                <i className="ri-map-pin-time-line"></i> {distance} k/m
                                            </span>
                                            <span>
                                                <i className="ri-group-line"></i> {maxGroupSize} people
                                            </span>
                                        </div>
                                        <h5>Description</h5>
                                        <p>{desc}</p>
                                    </div>
                                </div>

                                {/*=== Tour Reviews Section ===*/}
                                <div className="tour__reviews mt-4">
                                    <h4>Reviews ({reviews?.length})</h4>
                                    <form onSubmit={submitHandler}>
                                        <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                                            {[1, 2, 3, 4, 5].map((star) => (
                                                <span key={star} onClick={() => setTourRating(star)}>
                                                    {star} <i className="ri-star-s-fill"></i>
                                                </span>
                                            ))}
                                        </div>
                                        <div className="review__input">
                                            <input type="text" ref={reviewMsgRef} placeholder="Share your thoughts" required />
                                            <button className="btn primary__btn text-white" type="submit">
                                                Submit
                                            </button>
                                        </div>
                                    </form>
                                    <ListGroup className="user__reviews">
                                        {reviews?.map((review, index) => (
                                            <div className="review__item" key={index}>
                                                <img src={avatar} alt="User avatar" />
                                                <div className="w-100">
                                                    <div className="d-flex align-items-center justify-content-between">
                                                        <div>
                                                            <h5>Kanish</h5>
                                                            <p>{new Date("02-13-2025").toLocaleDateString("en-US", options)}</p>
                                                        </div>
                                                        <span className="d-flex align-items-center">
                                                            5 <i className="ri-star-s-fill"></i>
                                                        </span>
                                                    </div>
                                                    <h6>Amazing tour</h6>
                                                </div>
                                            </div>
                                        ))}
                                    </ListGroup>
                                </div>
                                {/*=== Tour Reviews Section End ===*/}
                            </Col>
                            <Col lg="4">
                                <Booking tour={tour} avgRating={avgRating} />
                            </Col>
                        </Row>
                    )}
                </Container>
            </section>
            <Newsletter />
        </>
    );
};

export default TourDetails;
