import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Container, Row, Col } from "reactstrap";
import "../styles/tour-details.css";
import calculateAvgRating from "../utils/avgRating";
import { fetchTours } from "../assets/data/tours"; // Import the API function

const TourDetails = () => {
    const { id } = useParams();
    
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchTours().then((data) => {
            const foundTour = data.find((tour) => tour.id.toString() === id.toString());
            setTour(foundTour);
            setLoading(false);
        });
    }, [id]);

    if (loading) {
        return <h2 className="text-center">Loading tour details...</h2>;
    }

    if (!tour) {
        return <h2 className="text-center">Tour not found</h2>;
    }

    const { photo, title, desc, price, address, reviews, city, maxGroupSize } = tour;
    const { totalRating, avgRating } = calculateAvgRating(reviews);

    return (
        <section>
            <Container>
                <Row>
                    <Col lg="8">
                        <div className="tour__content">
                            <img src={photo} alt={title} />

                            <div className="tour__info">
                                <h2>{title}</h2>
                                <div className="d-flex align-items-center gap-5">
                                    <span className="tour__rating d-flex align-items-center gap-1">
                                        <i className="ri-star-fill" style={{ color: "var(--secondary-color)" }}></i>
                                        {totalRating === 0 ? "Not Rated" : avgRating}
                                        {totalRating !== 0 && <span>({reviews?.length})</span>}
                                    </span>
                                    <span>
                                        <i className="ri-map-pin-user-line"></i> {address}
                                    </span>
                                </div>
                                <div className="tour__extra-details">
                                    <span>
                                        <i className="ri-map-pin-2-line"></i> {city}
                                    </span>
                                    <span>
                                        <i className="ri-money-dollar-circle-line"></i> ${price}
                                    </span>
                                    <span>
                                        <i className="ri-group-line"></i> {maxGroupSize}
                                    </span>
                                </div>
                                <p>{desc}</p>
                            </div>
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    );
};

export default TourDetails;
