import React, { useState, useEffect } from "react";
import CommonSection from "../shared/CommonSection";
import { Container, Row, Col } from "reactstrap";
import SearchBar from "../shared/SearchBar";
import Newsletter from "../shared/Newsletter";
import tourData from "../assets/data/tours"; // Ensure correct import
import TourCard from "../shared/TourCard";
import "../styles/tour.css";

import useFetch from '../hooks/useFetch'
import { BASE_URL } from "../utils/config";

const Tours = () => {
    const [pageCount, setPageCount] = useState(0);
    const [page, setPage] = useState(0);

    const { data: tours } = useFetch(`${BASE_URL}/tours`)
    const { data: tourCount } = useFetch(`${BASE_URL}/tours/search/getTourCount`)

    useEffect(() => {
        const pages = Math.ceil(tourCount.length / 4); // Dynamically set pages based on data length
        setPageCount(pages);
    }, [page]);

    return (
        <>
            <CommonSection title={"All Tours"} />
            <section>
                <Container>
                    <Row>
                        <SearchBar />
                    </Row>
                </Container>
            </section>

            <section className="pt-0">
                <Container>
                    <Row>
                        {/* 🔹 Display all tours dynamically */}
                        {tourData.map((tour) => (
                            <Col lg="3" md="6" sm="6" className="mb-4" key={tour.id}>
                                <TourCard tour={tour} />
                            </Col>
                        ))}

                        {/* 🔹 Pagination (optional) */}
                        <Col lg="12">
                            <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                                {[...Array(pageCount).keys()].map((number) => (
                                    <span
                                        key={number}
                                        onClick={() => setPage(number)}
                                        className={page === number ? "active__page" : ""}
                                    >
                                        {number + 1}
                                    </span>
                                ))}
                            </div>
                        </Col>
                    </Row>
                </Container>
            </section>

            <Newsletter />
        </>
    );
};

export default Tours;
