import React, { useState, useEffect } from "react";
import CommonSection from "../shared/CommonSection";
import "../styles/tour.css";
import tours from "../assets/data/tours"; // âœ… Correctly import as an array
import TourCard from "../shared/TourCard";
import SearchBar from "../shared/SearchBar";
import Newsletter from "../shared/Newsletter";
import { Container, Row, Col } from "reactstrap";

const Tours = () => {
    const [tourList, setTourList] = useState([]);
    const [loading, setLoading] = useState(true);
    const [pageCount, setPageCount] = useState(1);
    const [page, setPage] = useState(0);
    const itemsPerPage = 4;

    useEffect(() => {
        setLoading(true);
        setTourList(tours); // âœ… Assign the imported array directly
        setPageCount(Math.ceil(tours.length / itemsPerPage));
        setLoading(false);
    }, []);

    const paginatedTours = tourList.slice(page * itemsPerPage, (page + 1) * itemsPerPage);

    return (
        <>
            <CommonSection title="All Tours" />
            <section>
                <Container>
                    <Row>
                        <SearchBar />
                    </Row>
                </Container>
            </section>
            <section className="pt-0">
                <Container>
                    <Row>
                        {loading ? (
                            <h3 className="text-center">Loading tours...</h3>
                        ) : paginatedTours.length > 0 ? (
                            paginatedTours.map((tour) => (
                                <Col lg="3" className="mb-4" key={tour.id}>
                                    <TourCard tour={tour} />
                                </Col>
                            ))
                        ) : (
                            <h3 className="text-center">No tours available.</h3>
                        )}

                        <Col lg="12">
                            <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                                {[...Array(pageCount).keys()].map((number) => (
                                    <span
                                        key={number}
                                        onClick={() => setPage(number)}
                                        className={page === number ? "active__page" : ""}
                                    >
                                        {number + 1}
                                    </span>
                                ))}
                            </div>
                        </Col>
                    </Row>
                </Container>
            </section>
            <Newsletter />
        </>
    );
};

export default Tours;
