import React from "react";
import '../styles/tour-details.css';
import { Container, Row, Col } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from '../assets/data/tours';
import calculateAvgRating from "../utils/avgRating";

const TourDetails = () => {
    const { id } = useParams();  // Extract id from URL
    const tour = tourData.find(tour => String(tour.id) === String(id)); // Fix to match tour.id type

    // Handle if the tour is not found
    if (!tour) {
        return <div>Tour not found. Please check the URL or try again later.</div>;
    }

    const { photo, title, desc, price, address, reviews, city, distance, maxGroupSize } = tour;
    const { totalRating, avgRating } = calculateAvgRating(reviews); // Correctly use the avgRating calculation

    return (
        <>
            <section>
                <Container>
                    <Row>
                        <Col lg="8">
                            <div className="tour__content">
                                <img src={photo} alt={title} />

                                <div className="tour__info">
                                    <h2>{title}</h2>
                                    <div className="d-flex align-items-center gap-5">
                                        <span className="tour__rating d-flex align-items-center gap-1">
                                            <i className="ri-star-fill" style={{ 'color': "var(--secondary-color)" }}></i>
                                            {avgRating > 0 ? avgRating : "Not Rated"}
                                            {totalRating === 0 ? null : <span>({reviews?.length})</span>}
                                        </span>
                                        <span>
                                            <i className="ri-map-pin-user-line"></i>{address}
                                        </span>
                                    </div>
                                    <div className="tour__extra-details">
                                        <span>
                                            <i className="ri-map-pin-2-line"></i>{city}
                                        </span>
                                        <span>
                                            <i className="ri-money-dollar-circle-line"></i>${price}
                                        </span>
                                        <span>
                                            <i className="ri-group-line"></i>{maxGroupSize}
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </Col>
                    </Row>
                </Container>
            </section>
        </>
    );
};

export default TourDetails;
