import React from "react";
import '../styles/tour-details.css';
import { Container, Row, Col } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from '../assets/data/tours';
import calculateAvgRating from "../utils/avgRating";

const TourDetails = () => {
    const { id } = useParams();
    
    // Ensure the ID matches the stored format (e.g., "01" instead of "1")
    const formattedId = id.padStart(2, "0");
    const tour = tourData.find(tour => tour.id === formattedId);

    // Debugging logs
    console.log("URL ID:", id);
    console.log("Formatted ID:", formattedId);
    console.log("Available Tour IDs:", tourData.map(t => t.id));

    // If tour is not found, display a message
    if (!tour) {
        return <h2 className="text-center">ðŸš« Tour not found!</h2>;
    }

    const { photo, title, price, address, reviews, city, maxGroupSize } = tour;
    const { totalRating, avgRating } = calculateAvgRating(reviews);

    return (
        <>
            <section>
                <Container>
                    <Row>
                        <Col lg="8">
                            <div className="tour__content">
                                <img src={photo} alt={title} />
                                <div className="tour__info">
                                    <h2>{title}</h2>
                                    <div className="d-flex align-items-center gap-5">
                                        <span className="tour__rating d-flex align-items-center gap-1">
                                            <i className="ri-star-fill" style={{ color: "var(--secondary-color)" }}></i> 
                                            {avgRating === 0 ? "No Ratings" : avgRating}
                                            {totalRating === 0 ? " (Not Rated)" : <span>({reviews.length})</span>}
                                        </span>
                                        <span>
                                            <i className="ri-map-pin-user-line"></i> {address}
                                        </span>
                                    </div>
                                    <div className="tour__extra-details">
                                        <span><i className="ri-map-pin-2-line"></i> {city}</span>
                                        <span><i className="ri-money-dollar-circle-line"></i> ${price}</span>
                                        <span><i className="ri-group-line"></i> {maxGroupSize}</span>
                                    </div>
                                </div>
                            </div>
                        </Col>
                    </Row>
                </Container>
            </section>
        </>
    );
};

export default TourDetails;
