import React from "react";
import galleryImages from "./galleryImages";
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';

const MasonryImagesGallery = () => {
    const handleError = (e) => {
        e.target.src = "path/to/placeholder/image.jpg"; // Replace with actual placeholder image URL
    };

    return (
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 768: 3, 992: 4 }}>
            <Masonry gutter="1rem">
                {galleryImages.map((item, index) => (
                    <img
                        className="masonry__img"
                        src={item}
                        key={index}
                        alt={`Gallery Image ${index + 1}`}  // Dynamic alt text
                        onError={handleError}  // Handling image load errors
                        style={{
                            width: "100%",
                            display: "block",
                            borderRadius: "10px",
                            boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)", // Optional box shadow
                            transition: "transform 0.3s ease",
                        }}
                    />
                ))}
            </Masonry>
        </ResponsiveMasonry>
    );
};

export default MasonryImagesGallery;
